import React, { Component } from "react";
import axios from "axios";

export default class Index extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fileSelected: null,
      uploadId: "",
      fileName: "",
      backendUrl: "http://localhost:3000/api/shz",
    };
  }

  async fileHandler(event) {
    try {
      let fileSelected = event.target.files[0];
      let fileName = fileSelected.name;
      this.setState({ fileSelected });
      this.setState({ fileName });
    } catch (err) {
      console.error(err, err.message);
    }
  }

  async startUpload(event) {
    try {
      event.preventDefault();
      const CHUNK_SIZE = 1024 * 1000 * 20; // 20MB
      const fileSize = this.state.fileSelected.size;
      const CHUNKS_COUNT = Math.floor(fileSize / CHUNK_SIZE) + 1;

      const params = {
        videoName: this.state.fileName,
        scheduleId: 3506,
        deviceId: 75,
        logFile: "logFile.log",
        totalParts: CHUNKS_COUNT,
      };

      let resp = await axios.post(
        `${this.state.backendUrl}/video`,
        {},
        {
          data: { ...params },
        }
      );
      const { presignedLink } = resp.data.data;
      console.log("presignedLink", presignedLink);
      // this.setState({ uploadId });
      // this.uploadMultipartFile();
    } catch (err) {
      console.log(err);
    }
  }

  async uploadMultipartFile() {
    try {
      const CHUNK_SIZE = 1024 * 1000 * 20; // 20MB
      const fileSize = this.state.fileSelected.size;
      const CHUNKS_COUNT = Math.floor(fileSize / CHUNK_SIZE) + 1;
      let promisesArray = [];
      let start, end, blob;

      for (let index = 1; index < CHUNKS_COUNT + 1; index++) {
        start = (index - 1) * CHUNK_SIZE;
        end = index * CHUNK_SIZE;
        blob =
          index < CHUNKS_COUNT
            ? this.state.fileSelected.slice(start, end)
            : this.state.fileSelected.slice(start);

        // Get presigned URL for each part
        let getUploadUrlResp = await axios.post(
          `${this.state.backendUrl}/get-upload-url`,
          {},
          {
            data: {
              videoName: this.state.fileName,
              partNumber: index,
              uploadId: this.state.uploadId,
            },
          }
        );
        let { presignedUrl } = getUploadUrlResp.data;

        console.log(`PresignedUrl ${index}`, presignedUrl);

        let uploadResp = axios.put(presignedUrl, blob, {
          headers: {
            "Content-Type": this.state.fileSelected.type,
          },
        });
        promisesArray.push(uploadResp);
      }

      let resolvedArray = await Promise.all(promisesArray);
      console.log("resolvedArray", resolvedArray);

      let uploadPartsArray = [];
      resolvedArray.forEach((resolvedPromise, index) => {
        uploadPartsArray.push({
          ETag: resolvedPromise.headers.etag,
          PartNumber: index + 1,
        });
      });
      console.log("Parts", uploadPartsArray);

      let completeUploadResp = await axios.post(
        `${this.state.backendUrl}/complete-upload`,
        {},
        {
          data: {
            videoName: this.state.fileName,
            parts: uploadPartsArray,
            uploadId: this.state.uploadId,
          },
        }
      );

      console.log(completeUploadResp.data, "complete upload response");
    } catch (err) {
      console.log(err);
    }
  }

  async getData() {
    const resp = await axios.get(
      `${this.state.backendUrl}/api/v1/file/62c3b627d34caccddce9dbad`
    );
    console.log(resp);
  }

  componentDidMount() {
    // this.getData();
  }
  render() {
    return (
      <div style={{ backgroundColor: "grey", height: 1500 }}>
        <form onSubmit={this.startUpload.bind(this)}>
          <div>
            <p>Upload Dataset:</p>
            <input
              type="file"
              id="file"
              onChange={this.fileHandler.bind(this)}
            />
            <button type="submit">Upload</button>
          </div>
        </form>
      </div>
    );
  }
}
